INSERT INTO `email_templates` (`id`, `name`, `subject`, `help`, `body`) VALUES ('2', 'Forgot Password Email', 'Password Reset', 'This template is used for retrieving lost user password', '&lt;div style=&quot;color:#000;margin-top:20px;margin-left:auto;margin-right:auto;max-width:800px;background-color:#F4F4F4&quot;&gt;\n  &lt;table style=&quot;font-family: Helvetica Neue,Helvetica,Arial, sans-serif; font-size:13px;background: #F4F4F4; width: 100%; border: 4px solid #bbbbbb;&quot; cellpadding=&quot;10&quot; cellspacing=&quot;5&quot;&gt;\n&lt;tbody&gt;\n&lt;tr&gt;\n&lt;th style=&quot;background-color:#ccc; font-size:16px;padding:5px;border-bottom:2px solid #fff;&quot;&gt;\nNew password reset from [SITE_NAME]!\n&lt;/th&gt;\n&lt;/tr&gt;\n&lt;tr&gt;\n&lt;td style=&quot;text-align: left;&quot; valign=&quot;top&quot;&gt;\nHello, &lt;b&gt;[USERNAME]&lt;/b&gt;&lt;br&gt;\n&lt;br&gt;\nIt seems that you or someone requested a new password for you.\n&lt;br&gt;\nWe have generated a new password, as requested:\n&lt;br&gt;\n&lt;br&gt;\nYour new password: \n&lt;b&gt;[PASSWORD]&lt;/b&gt;&lt;br&gt;\n&lt;br&gt;\nTo use the new password you need to activate it. To do this click the link provided below and login with your new password.\n&lt;br&gt;\n&lt;a href=&quot;[LINK]&quot;&gt;[LINK]&lt;/a&gt;&lt;br&gt;\n&lt;br&gt;\nYou can change your password after you sign in.\n&lt;hr&gt;\nPassword requested from IP: [IP]\n&lt;/td&gt;\n&lt;/tr&gt;\n&lt;tr&gt;\n&lt;td style=&quot;text-align: left; background-color:#fff;border-top-width:2px; border-top-color:#ccc; border-top-style:solid&quot; valign=&quot;top&quot;&gt;\n&lt;i&gt;Thanks,&lt;br&gt;\n[SITE_NAME] Team\n&lt;br&gt;\n&lt;a href=&quot;[URL]&quot;&gt;[URL]&lt;/a&gt;&lt;/i&gt;\n&lt;/td&gt;\n&lt;/tr&gt;\n&lt;/tbody&gt;\n&lt;/table&gt;\n&lt;/div&gt;');
