INSERT INTO `email_templates` (`id`, `name`, `subject`, `help`, `body`) VALUES ('15', 'Account Activation', 'Your account have been activated', 'This template is used to notify user when manual account activation is completed', '&lt;div style=&quot;color:#000;margin-top:20px;margin-left:auto;margin-right:auto;max-width:800px;background-color:#F4F4F4&quot;&gt;\n&lt;table style=&quot;font-family: Helvetica Neue,Helvetica,Arial, sans-serif; font-size:13px;background: #F4F4F4; width: 100%; border: 4px solid #bbbbbb;&quot; cellpadding=&quot;10&quot; cellspacing=&quot;5&quot;&gt;\n&lt;tbody&gt;\n&lt;tr&gt;\n&lt;th style=&quot;background-color:#ccc; font-size:16px;padding:5px;border-bottom:2px solid #fff;&quot;&gt;\nHello, [NAME]! &lt;br&gt;\n&lt;/th&gt;\n&lt;/tr&gt;\n&lt;tr&gt;\n&lt;td style=&quot;text-align: left;&quot; valign=&quot;top&quot;&gt;\nHello,&lt;br&gt;\n&lt;br&gt;\n  You&#039;re now a member of [SITE_NAME].\n&lt;br&gt;\n&lt;br&gt;\n  Your account is now fully activated\n  , and you may login at \n&lt;a href=&quot;[URL]&quot;&gt;[URL]&lt;/a&gt;\n&lt;hr&gt;\n&lt;/td&gt;\n&lt;/tr&gt;\n&lt;tr&gt;\n&lt;td style=&quot;text-align: left;&quot;&gt;\n&lt;i&gt;Thanks,&lt;br&gt;\n  [SITE_NAME] Team\n&lt;br&gt;\n&lt;a href=&quot;[URL]&quot;&gt;[URL]&lt;/a&gt;&lt;/i&gt;\n&lt;/td&gt;\n&lt;/tr&gt;\n&lt;/tbody&gt;\n&lt;/table&gt;\n&lt;/div&gt;');


